                string expectedSQL = "(SELECT *, \"INV_COMPOUNDS\".\"BASE64_CDX\" AS \"fieldName\" FROM CHEMINVDB2.INV_COMPOUNDS \"INV_COMPOUNDS\", CHEMINVDB2.INV_CONTAINERS \"c\", CHEMINVDB2.CSDOHITLIST \"h\" WHERE \"INV_COMPOUNDS\".\"COMPOUND_ID\" = \"c\".\"COMPOUND_ID_FK\"(+) AND \"c\".\"CONTAINER_ID\" = \"h\".\"ID\"(+) AND (\"c\".\"QTY_INITIAL\" = :0) AND (TRIM(\"c\".\"BARCODE\") <> :1) AND (LTRIM(\"c\".\"BARCODE\") > :2) AND (RTRIM(\"c\".\"BARCODE\") < :3) AND (\"c\".\"LOCATION_ID_FK\" BETWEEN :4 AND :5) AND (\"c\".\"BARCODE\" BETWEEN :6 AND :7) AND TRIM(\"h\".\"HITLISTID\") IN(SELECT /*+ cardinality(t 10)*/ * FROM TABLE(CAST(COEDB.COEDBLibrary.ClobToTable(:8) as COEDB.MYTABLETYPE)) t WHERE ROWNUM >= 0) )";
