alter table COEDB.CHEM_REG_PRIVILEGES add (EDIT_SHARED_COMPONENTS NUMBER(1) default 0,EDIT_SHARED_STRUCTURES  NUMBER(1) default 0,CREATE_DUP_COMPONENT NUMBER(1) default 0,CREATE_DUP_REGISTRY NUMBER(1) default 0) ;
UPDATE COEDB.CHEM_REG_PRIVILEGES SET EDIT_SHARED_COMPONENTS=1 , EDIT_SHARED_STRUCTURES=1, CREATE_DUP_REGISTRY=1, CREATE_DUP_COMPONENT=1, SET_LOCKED_FLAG =1, TOGGLE_LOCKED_FLAG=1 WHERE ROLE_INTERNAL_ID =(SELECT ROLE_ID FROM SECURITY_ROLES WHERE ROLE_NAME='SUPERVISING_CHEMICAL_ADMIN');
UPDATE COEDB.CHEM_REG_PRIVILEGES SET EDIT_SHARED_COMPONENTS=1 , EDIT_SHARED_STRUCTURES=1, CREATE_DUP_REGISTRY=1, CREATE_DUP_COMPONENT=1, SET_LOCKED_FLAG =1 WHERE ROLE_INTERNAL_ID =(SELECT ROLE_ID FROM SECURITY_ROLES WHERE ROLE_NAME='CHEMICAL_ADMINISTRATOR');
UPDATE COEDB.CHEM_REG_PRIVILEGES SET CREATE_DUP_REGISTRY=1, CREATE_DUP_COMPONENT=1 WHERE ROLE_INTERNAL_ID =(SELECT ROLE_ID FROM SECURITY_ROLES WHERE ROLE_NAME='SUPERVISING_SCIENTIST');
/