--Create formgroup roles
DECLARE                                                                                                                                                                                                                                                                                                                                                                                                         
 	PROCEDURE CreateRole(roleName IN varchar2) IS                                                                                                                                                                                                                                                                                                                                                                 
		n NUMBER;                                                                                                                                                                                                                                                                                                                                                                                                     
	BEGIN                                                                                                                                                                                                                                                                                                                                                                                                          
		select count(*) into n from dba_roles where Upper(role) = roleName;                                                                                                                                                                                                                                                                                                                                           
		if n > 0 then                                                                                                                                                                                                                                                                                                                                                                                                 
		   execute immediate 'DROP ROLE ' || roleName;                                                                                                                                                                                                                                                                                                                                                                
		end if;                                                                                                                                                                                                                                                                                                                                                                                                       
		execute immediate 'CREATE ROLE ' || roleName|| ' NOT IDENTIFIED';                                                                                                                                                                                                                                                                                                                                             
	END CreateRole;                                                                                                                                                                                                                                                                                                                                                                                                
